CREATE TABLE director(id INT PRIMARY KEY AUTO_INCREMENT, name VARCHAR(50), birth YEAR);
CREATE TABLE studio(name VARCHAR(20) PRIMARY KEY, city VARCHAR(20), pics VARCHAR(50));
CREATE TABLE actor(id INT PRIMARY KEY AUTO_INCREMENT, name VARCHAR(50), nationality VARCHAR(20), birth YEAR);
CREATE TABLE movie(title VARCHAR(100) PRIMARY KEY, release_date INT, genre VARCHAR(100), hossz INT, rank INT, directorID INT, FOREIGN KEY (directorID) REFERENCES director(id) ON DELETE CASCADE ON UPDATE CASCADE, studioID VARCHAR(20), FOREIGN KEY (studioID)  REFERENCES studio(name) ON DELETE CASCADE ON UPDATE CASCADE);
CREATE TABLE role(actorID INT, FOREIGN KEY (actorID) REFERENCES actor(id) ON DELETE CASCADE ON UPDATE CASCADE, movieID VARCHAR(100), FOREIGN KEY (movieID)  REFERENCES movie(title) ON DELETE CASCADE ON UPDATE CASCADE);